        }

        &__title {
            font-size: 40px;
            line-height: 1;
            font-weight: 700;
        }

        &__price {
            margin-right: 16px;
            font-size: 32px;
            line-height: 150%;
            font-weight: 500;
            letter-spacing: 0.03em;
        }

        &__old-price {
            font-size: 24px;
            line-height: 133%;
            letter-spacing: 0.03em;
            text-decoration: line-through;
            color: #a0a0a0;
        }

        &__color-text {
            margin-right: 24px;
            font-size: 15px;
            line-height: 160%;
            color: #0c0c0c;
        }

        &__select-color {
            gap: 8px;
        }

        &__color input[type=radio] {
            display: none;
        }

        &__color label {
            display: inline-block;
            cursor: pointer;
            position: relative;
            user-select: none;
        }

        &__color label {
            display: inline-block;
            border-radius: 50%;
            width: 32px;
            height: 32px;
            user-select: none;
            cursor: pointer;
        }

        .black {
            background: #000;
        }

        .violet {
            background: #781dbc;
        }

        .red {
            background: #e10000;
        }

        .yellow {
            background: #e1b000;
        }

        .gray {
            background: #e8e8e8;
        }
        
        &__color input[type=radio]:checked + label {
            border: 1px solid;
        }

